mapper.select:
select ort.rn, ort.clubId, ort.clubName, ort.intro1, ort.img2 from(select rownum rn, ot.clubId, ot.clubName, ot.intro1, ot.img2 from(select c.clubId, c.clubName, c.intro1, c.img2 from club c, club_category b, (select c.clubId, LISTAGG( to_char(t.tagNo, '099'), ',') tagNos from club_tag c, tag t where c.tagNo = t.tagNo group by c.clubId )ts where c.clubId = ts.clubId and c.clubId = b.clubId
and c.address1 = #{address1}
and c.address2 = #{address2}
and b.cateNo = #{cateNo}
and ts.tagNos like '%'||to_char(#{no}, '099')||'%'
and c.clubName like '%'|| #{keyword} ||'%'
order by clubId desc)ot )ort where rn >= #{startRnum} and rn <= #{endRnum}

select ort.rn, ort.clubId, ort.clubName, ort.intro1, ort.img2 from ( select rownum rn, ot.clubId, ot.clubName, ot.intro1, ot.img2 from ( select a.clubId, a.clubName, a.intro1, a.img2 from club a , club_category b where a.clubId = b.clubId
and a.clubName like '%'|| #{keyword} ||'%'
order by clubId desc ) ot ) ort where rn>=#{startRnum} and rn<=#{endRnum}



select clubId,memberId,qnaCotent,answerContent
from club_qna
where clubId = #{clubId}


select count (*)
from club_qna
where clubId = #{clubId}

mapper.update:

update club_qna 
set 
	answerContent = #{answerContent} 
	,answerDate = sysdate 
where qnaNo = #{qnaNo}
