mapper.select:

select memberId
from member
where memberId=#{memberId}


select
	tagName
from(
	select 
		tagName, rank() over (order by a.tagNo) as tagRank
	from member_tag a 
	join tag b on a.tagNo = b.tagNo 
	where memberId = #{memberId}
)
where tagRank <= 3

mapper.update:

update club_member 
	set 
	memLevel = #{memLevel}
	where clubMemNo = #{clubmemNo}


update club set 
	preNum = preNum+1
where clubId = #{clubId}
