mapper.select:
select b.aboardNo as boardNo ,b.aboardval as meetCate ,a.clubId as clubId ,c.clubName as clubName , case when aboardval = 2 then '(결제)' when aboardval = 3 then '(모임)' end as frontTitle ,b.title as title ,TO_CHAR(startDate, 'YYYY-MM-DD') AS startDate ,TO_CHAR(endDate, 'YYYY-MM-DD') AS endDate from meet_name a join aboard b on a.meetNo = b.meetNo join club c on c.clubId = b.clubId join club_member d on c.clubId = d.clubId where d.memberId = #{memberId} and meetyear in #{years} and meetmon= #{month}
and b.aboardVal = 2
and b.aboardVal in (2,3)
and b.aboardVal = 3
and c.clubId = #{viewOption2}

mapper.insert:

			insert into my_schedule 
    			(
    			MYSCHEDULENO
    			,MEMBERID
    			,STARTDATE
   				,ENDDATE
  			    ,SCHETITLE
   		    	,SCHECONTENT


   			    ,SCHEPLACE
    			,ADDRESS1
    			,ADDRESS2


    			)

			values(
    			seq_mysche_no.nextval
    			,#{memberId}
    			,#{startDate}
    			,#{endDate}
   				,#{title}
   				,#{content}


   			    ,#{place}
   	 			,#{address1}
    			,#{address2}

		    )

mapper.update:

update  my_schedule 
	set 
	STARTDATE  = #{startDate}
	, ENDDATE = #{endDate}
	,SCHETITLE = #{title}
	, SCHECONTENT = #{content}
	, SCHEPLACE = #{place}
	, ADDRESS1 = #{address1}
	, ADDRESS2 = #{address2}
where MYSCHEDULENO = ${perScheNo}
